import Cart from './components/Cart/Cart';
import Header from './components/Layout/Header';
import Repas from './components/Repas/Repas';
function App() {
  const [modalIsShow, setModalIsShow] = useEffect(false);
  const showHandler = () => {
    setModalIsShow(true);
  };
  const closeHandler = () => {
    setModalIsShow(false);
  };
  return (
    <Fragment>
      <Cart />
      <Header />
      <main>
        <Repas />
      </main>
    </Fragment>
  );
}

export default App;
